import React, {useEffect, useState } from 'react'
import "../CSS/HomeFeed.css"
import Avatar from '@material-ui/core/Avatar';
import FavoriteIcon from '@material-ui/icons/Favorite';
import ChatBubbleIcon from '@material-ui/icons/ChatBubble';
import LoopIcon from '@material-ui/icons/Loop';
import PublishIcon from '@material-ui/icons/Publish';
import TweetWrite from './TweetWrite'
import axios from 'axios'
const HomeFeed = () => {
    const [tweets, setTweets] = useState([]);
    const [mounted, setMounted] = useState(true);

    useEffect(()=>{
        const loadData =  async function(){
            const url = "http://localhost:5000/api/feeds"
            const {data} = await axios.get(url)
            if(mounted){
                setTweets(data)
            }
        }

        loadData();
        return ()=>{
            setMounted(false)
        };
    }, [mounted]);
console.log(tweets);
    return (
        <div className="homeFeedContainer">
            <TweetWrite />

            <div className="feed">{
                tweets && <div className="feeds">
                    {
                        tweets.map(tweet=>{
                            return( <div className="feedContent">
                                <div className="userProf">
                                <Avatar alt="User" />
                                </div>
                                <div className="tweet">
                                    <div className="user">
                                        <h4>User </h4>
                                        <h4 className="userTag">@user</h4>
                                    </div>
                                    <h4 className="text">{tweet.tweet}</h4>
                                    {
                                        tweet.file ? <img src={tweet.file} alt="" width="500" /> : null
                                    }
                                    <div className="tweetIcons">
                                        <div className="comment">
                                            <ChatBubbleIcon/>
                                            <h5>{tweet.comments.length}</h5>
                                        </div>
                                        <div className="retweet">
                                            <LoopIcon/>
                                        </div>
                                        <div className="likes">
                                            <FavoriteIcon/>
                                            <h5>{tweet.likes}</h5>
                                        </div>
                                        <div className="share">
                                            <PublishIcon />
                                        </div>
                                    </div>
                                </div>

                            </div>)
                        })
                    }
                </div>
            }
            </div>
        </div>
    )
}

export default HomeFeed
